wizard.saveScript:
//search groups by group name
var searchedGroup = user.searchGroups('mini').get(0);
xml.set('item/searchedGroup',searchedGroup);

//get the logged in user belongs to
var userGroup = user.getGroups().get(0);
xml.set('item/userGroup',userGroup);
xml.set('item/isMemberOfGroup',user.isMemberOfGroup(userGroup.getUniqueID()));
xml.set('item/isNotMemberOfGroup', user.isMemberOfGroup(searchedGroup.getUniqueID()));
xml.set('item/containingUserGroup',user.getGroupsContainingUser(user.getID()).get(0));

var userInGroup = user.searchUsersInGroups('Vaekdea',userGroup.getUniqueID(),true);
xml.set('item/userInGroup',userInGroup.get(0));

//search roles
var internalRole = user.searchRoles('a role').get(0);
xml.set('item/internalRole',internalRole);
xml.set('item/hasInternalRole',user.hasRole(internalRole.getUniqueID()));

var externalRole = user.searchRoles('administrator').get(0);
xml.set('item/externalRole',externalRole);
xml.set('item/hasExternalRole',user.hasRole(externalRole.getUniqueID()));

var groupIds = [searchedGroup.getUniqueID()];
xml.set('item/getInformationForGroups',user.getInformationForGroups(groupIds).get(0).getName());

var userIds = [user.getID()];
xml.set('item/getInformationForUsers',user.getInformationForUsers(userIds).get(0).getUsername());
