wizard.saveScript:
/* add save script to add status = copyright for any portion where status is blank 

var prop = xml.getPropBagEx();
*/
var xmlTypeArray = xml.getAllSubtrees('/item/copyright/portions/portion/sections/section/');
for(var i=0; i < xmlTypeArray.length; i++)
{
    var xt = xmlTypeArray[i];
    var thisStatus = xt.get('copyrightstatus');
    if (thisStatus=='')
    {
      xt.set('copyrightstatus','copyright');
    }
}
